public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x; //Declaration
		x = 20; //Assignment
		int y = 10; //Initialization
		
		long lNum = 1348615948461684521L;
		
		float z = 3.14f;
		double pi = 3.14;
		
		boolean bool = true;
		
		System.out.println(bool);
		System.out.println(y);
		System.out.println("Value of X is: " + x);
		System.out.println("An extremely large number is = " + lNum);
		System.out.println("Float value"+ z);
		System.out.println("Double value"+ pi);

	}

}
